  

// TypeScript Version: 4.1

/**
* Returns the median of a chi-squared distribution.
*
* ## Notes
*
* -   If provided `k < 0`, the function returns `NaN`.
*
* @param k - degrees of freedom
* @returns median
*
* @example
* var v = median( 9.0 );
* // returns ~8.343
*
* @example
* var v = median( 2.0 );
* // returns ~1.386
*
* @example
* var k = median( -0.2 );
* // returns NaN
*
* @example
* var k = median( NaN );
* // returns NaN
*/
declare function median( k: number ): number;


// EXPORTS //

export = median;
