  

'use strict';

var randint = require( '@stdlib/random/base/discrete-uniform' );
var entropy = require( './../lib' );

var randa = randint.factory( 0, 10 );
var randb = randint.factory();
var a;
var b;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	a = randa();
	b = randb( a, a+randa() );
	v = entropy( a, b );
	console.log( 'a: %d, b: %d, H(X;a,b): %d', a.toFixed( 4 ), b.toFixed( 4 ), v.toFixed( 4 ) );
}
