  

'use strict';

var Exponential = require( './../lib' );

var exponential = new Exponential( 2.0 );

var mu = exponential.mean;
console.log( 'Mean = %d', mu );
// => 'Mean = 0.5'

var mode = exponential.mode;
console.log( 'Mode = %d', mode );
// => 'Mode = 0'

var s2 = exponential.variance;
console.log( 'Variance = %d', s2 );
// => 'Variance = 0.25'

var y = exponential.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => 'F(0.8) = 0.7981034820053446'
