  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/float64/eps' );
var mode = require( './../lib' );

var d1;
var d2;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	d1 = ( randu()*10.0 ) + EPS;
	d2 = ( randu()*10.0 ) + EPS;
	v = mode( d1, d2 );
	console.log( 'd1: %d, d2: %d, mode(X;d1,d2): %d', d1.toFixed( 4 ), d2.toFixed( 4 ), v.toFixed( 4 ) );
}
