  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var d1;
var d2;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	d1 = randu() * 10.0;
	d2 = randu() * 10.0;
	y = quantile( p, d1, d2 );
	console.log( 'p: %d, d1: %d, d2: %d, Q(p;d1,d2): %d', p.toFixed( 4 ), d1.toFixed( 4 ), d2.toFixed( 4 ), y.toFixed( 4 ) );
}
