  

'use strict';

// MODULES //

var tape = require( 'tape' );
var abs = require( '@stdlib/math/base/special/abs' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var EPS = require( '@stdlib/constants/float64/eps' );
var stdev = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof stdev, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var v = stdev( NaN, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = stdev( 10.0, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided `d1 <= 0`, the function returns `NaN`', function test( t ) {
	var y;

	y = stdev( -1.0, 2.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NINF, 1.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NINF, PINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NINF, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NINF, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided `d2 <= 4`, the function returns `NaN`', function test( t ) {
	var y;

	y = stdev( 2.0, 4.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( 2.0, 3.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( 2.0, 2.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( 2.0, 1.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( 2.0, -1.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( 1.0, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( PINF, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NINF, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = stdev( NaN, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the standard deviation of an F distribution', function test( t ) {
	var expected;
	var delta;
	var tol;
	var d1;
	var d2;
	var i;
	var y;

	expected = data.expected;
	d1 = data.d1;
	d2 = data.d2;
	for ( i = 0; i < expected.length; i++ ) {
		y = stdev( d1[i], d2[i] );
		if ( y === expected[i] ) {
			t.equal( y, expected[i], 'd1: '+d1[i]+', d2: '+d2[i]+', y: '+y+', expected: '+expected[i] );
		} else {
			delta = abs( y - expected[ i ] );
			tol = 2.0 * EPS * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. d1: '+d1[i]+'. d2: '+d2[i]+'. y: '+y+'. E: '+expected[ i ]+'. Î”: '+delta+'. tol: '+tol+'.' );
		}
	}
	t.end();
});
