  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var logpdf = require( './../lib' );

var alpha;
var s;
var m;
var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	alpha = randu() * 10.0;
	x = randu() * 10.0;
	s = randu() * 10.0;
	m = randu() * 10.0;
	y = logpdf( x, alpha, s, m );
	console.log( 'x: %d, Î±: %d, s: %d, m: %d, ln(f(x;Î±,s,m)): %d', x.toFixed( 4 ), alpha.toFixed( 4 ), s.toFixed( 4 ), m.toFixed( 4 ), y.toFixed( 4 ) );
}
