  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var alpha;
var s;
var m;
var p;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	alpha = randu() * 10.0;
	p = randu();
	s = randu() * 10.0;
	m = randu() * 10.0;
	y = quantile( p, alpha, s, m );
	console.log( 'p: %d, Î±: %d, s: %d, m: %d, Q(p;Î±,s,m): %d', p.toFixed( 4 ), alpha.toFixed( 4 ), s.toFixed( 4 ), m.toFixed( 4 ), y.toFixed( 4 ) );
}
