  

'use strict';

var Geometric = require( './../lib' );

var geometric = new Geometric( 0.5 );

var mu = geometric.mean;
console.log( 'Mean = %d', mu );
// => 'Mean = 1'

var mode = geometric.mode;
console.log( 'Mode = %d', mode );
// => 'Mode = 0'

var s2 = geometric.variance;
console.log( 'Variance = %d', s2 );
// => 'Variance = 2'

var y = geometric.cdf( 2.0 );
console.log( 'F(2.0) = %d', y );
// => 'F(2.0) = 0.875'
