  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var stdev = require( './../lib' );

var beta;
var mu;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	mu = ( randu()*10.0 ) - 5.0;
	beta = randu() * 20.0;
	y = stdev( mu, beta );
	console.log( 'µ: %d, β: %d, SD(X;µ,β): %d', mu.toFixed( 4 ), beta.toFixed( 4 ), y.toFixed( 4 ) );
}
