  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var skewness = require( './../lib' );

var v;
var i;
var N;
var K;
var n;

for ( i = 0; i < 10; i++ ) {
	N = round( randu() * 20 );
	K = round( randu() * N );
	n = round( randu() * K );
	v = skewness( N, K, n );
	console.log( 'N: %d, K: %d, n: %d, skew(X;N,K,n): %d', N, K, n, v.toFixed( 4 ) );
}
