  

'use strict';

// MODULES //

var tape = require( 'tape' );
var objectKeys = require( '@stdlib/utils/keys' );
var laplace = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof laplace, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains Laplace (double exponential) distribution functions', function test( t ) {
	var keys = objectKeys( laplace );
	t.equal( keys.length > 0, true, 'has keys' );
	t.end();
});
