  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var logpmf = require( './../lib' );

var i;
var r;
var p;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 30.0 );
	r = randu() * 50.0;
	p = randu();
	y = logpmf( x, r, p );
	console.log( 'x: %d, r: %d, p: %d, ln(P(X=x;r,p)): %d', x, r, p.toFixed( 4 ), y.toFixed( 4 ) );
}
