  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var mode = require( './../lib' );

var v;
var i;
var r;
var p;

for ( i = 0; i < 10; i++ ) {
	r = randu() * 100;
	p = randu();
	v = mode( r, p );
	console.log( 'r: %d, p: %d, mode(X;r,p): %d', r, p.toFixed( 4 ), v.toFixed( 4 ) );
}
