  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var mgf = require( './../lib' );

var sigma;
var mu;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu();
	mu = (randu() * 10.0) - 5.0;
	sigma = randu() * 20.0;
	y = mgf( t, mu, sigma );
	console.log( 't: %d, µ: %d, σ: %d, M_X(t;µ,σ): %d', t.toFixed( 4 ), mu.toFixed( 4 ), sigma.toFixed( 4 ), y.toFixed( 4 ) );
}
