  'use strict';

var ceil = require( '@stdlib/math/base/special/ceil' );
var randu = require( '@stdlib/random/base/randu' );
var pdf = require( './../lib' );

var n;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 30.0;
	n = ceil( randu() * 30.0 );
	y = pdf( x, n );
	console.log( 'x: %d, n: %d, F(x;n): %d', x.toFixed( 4 ), n.toFixed( 4 ), y.toFixed( 4 ) );
}
