  'use strict';

var randint = require( '@stdlib/random/base/discrete-uniform' );
var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var n;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	n = randint( 1, 20 );
	y = quantile( p, n );
	console.log( 'p: %d, n: %d, Q(p;n): %d', p.toFixed( 4 ), n, y.toFixed( 4 ) );
}
