  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var cdf = require( './../lib' );

var v;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = (randu() * 6.0) - 3.0;
	v = randu() * 10.0;
	y = cdf( x, v );
	console.log( 'x: %d, v: %d, F(x;v): %d', x.toFixed( 4 ), v.toFixed( 4 ), y.toFixed( 4 ) );
}
