  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var entropy = require( './../lib' );

var a;
var b;
var c;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	a = ( randu()*10.0 );
	b = ( randu()*10.0 ) + a;
	c = ( randu()*( b-a ) ) + a;
	v = entropy( a, b, c );
	console.log( 'a: %d, b: %d, c: %d, h(X;a,b,c): %d', a.toFixed( 4 ), b.toFixed( 4 ), c.toFixed( 4 ), v.toFixed( 4 ) );
}
