  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var mgf = require( './../lib' );

var a;
var b;
var c;
var t;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu() * 5.0;
	a = randu() * 10.0;
	b = a + (randu() * 40.0);
	c = a + (( b - a ) * randu());
	v = mgf( t, a, b, c );
	console.log( 't: %d, a: %d, b: %d, c: %d, M_X(t;a,b,c): %d', t.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), c.toFixed( 4 ), v.toFixed( 4 ) );
}
