  

'use strict';

// MODULES //

var tape = require( 'tape' );
var objectKeys = require( '@stdlib/utils/keys' );
var truncatedNormal = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof truncatedNormal, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains truncated normal distribution functions', function test( t ) {
	var keys = objectKeys( truncatedNormal );
	t.equal( keys.length > 0, true, 'has keys' );
	t.end();
});
