  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var a;
var b;
var p;
var y;
var i;

for ( i = 0; i < 25; i++ ) {
	p = randu();
	a = (randu() * 20.0) - 20.0;
	b = a + (randu() * 40.0);
	y = quantile( p, a, b );
	console.log( 'p: %d, a: %d, b: %d, Q(p;a,b): %d', p.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), y.toFixed( 4 ) );
}
