  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/float64/eps' );
var entropy = require( './../lib' );

var lambda;
var k;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	k = ( randu()*10.0 ) + EPS;
	lambda = ( randu()*10.0 ) + EPS;
	v = entropy( k, lambda );
	console.log( 'k: %d, Î»: %d, h(X;k,Î»): %d', k.toFixed( 4 ), lambda.toFixed( 4 ), v.toFixed( 4 ) );
}
