  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/float64/eps' );
var mgf = require( './../lib' );

var lambda;
var k;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu() * 5.0;
	lambda = ( randu() * 10.0 ) + EPS;
	k = ( randu() * 10.0 ) + EPS;
	y = mgf( t, k, lambda );
	console.log( 'x: %d, k: %d, Î»: %d, M_X(t;k,Î»): %d', t.toFixed( 4 ), k.toFixed( 4 ), lambda.toFixed( 4 ), y.toFixed( 4 ) );
}
